import { useEffect, useState } from 'react';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';
import ConnectionScreen from './components/ConnectionScreen.js';

function Connection() {
  const [roomData, setRoomData] = useState(null);

  useEffect(() => {
    let hostId = localStorage.getItem('host_id');

    if (!hostId) {
      hostId = uuidv4();
      localStorage.setItem('host_id', hostId);
    }

     // Проверяем, есть ли уже комната для этого hostId с активным статусом
     axios.get(`http://127.0.0.1:8000/api/rooms/check/${hostId}`)
     .then(response => {
       if (response.data.exists) {
         setRoomData(response.data.room); // Если комната существует, загрузим ее данные
       } else {
         // Если комнаты нет, создаем новую
         axios.post('http://127.0.0.1:8000/api/rooms/create/', {
           host: hostId
         })
         .then(response => {
           setRoomData(response.data);
         })
         .catch(error => {
           console.error("Ошибка создания комнаты", error);
         });
       }
     })
     .catch(error => {
       console.error("Ошибка при проверке комнаты", error);
     });
 }, []);

  return (
    <div className="Connection">
      {roomData ? (
        <div>
          <h2>Комната создана!</h2>
          <p>Код комнаты: {roomData.code}</p>
          <p>Ссылка: http://localhost:3000/join/{roomData.code}</p>
          {/* QR-код можно добавить позже */}
        </div>
      ) : (
        <p>Создание комнаты...</p>
      )}
      <ConnectionScreen />
    </div>
  );
}

export default Connection;
