import React, {useState, useEffect} from 'react';
import "../styles/PlayingCards.css";
import axios from 'axios';
import PlayingSpecialCards from "./PlayingSpecialCards.js";
import Modal from 'react-modal';

Modal.setAppElement('#root');

const PlayingCards = ({roomCode, card, action, setSelectedCard}) => {
    const player = JSON.parse(localStorage.getItem('player'));

    const [players, setPlayers] = useState([]);
    const [isSpecial, setIsSpecial] = useState(false);

    const [epidemicOptions, setEpidemicOptions] = useState([]);
    const [showEpidemicModal, setShowEpidemicModal] = useState(false);

    const [courtCardsOptions, setCourtCardsOptions] = useState([]);
    const [playerIdWhereKill, setPlayerIdWhereKill] = useState();
    const [showCourtCardsModal, setShowCourtCardsModal] = useState(false);

    const [peasantCardsOptions, setPeasantCardsOptions] = useState([]);
    const [playerIdToMovePeasantCards, setPlayerIdToMovePeasantCards] = useState();
    const [showPeasantCardsModal, setShowPeasantCardsModal] = useState(false);
    const [selectedCardsRadio, setSelectedCardsRadio] = useState({});

    const [playerIdToMove, setPlayerIdToMove] = useState();

    const [monksOptions, setMonksOptions] = useState([]);
    const [showMonksModal, setShowMonksModal] = useState(false);

    const [commonerCrusaderCardsOptions, setCommonerCrusaderCardsOptions] = useState([]);
    const [showCommonerCrusaderCardsModal, setShowCommonerCrusaderCardsModal] = useState(false);
    
    const [availableGroups, setAvailableGroups] = useState([]);
    const [showAvailableGroupsModal, setShowAvailableGroupsModal] = useState(false);
    const [nextPlayer, setNextPlayer] = useState([]);
    const [prevPlayer, setPrevPlayer] = useState([]);

    const [cardsGroupNeighbors, setCardsGroupNeighbors] = useState();
    const [showCardsGroupNeighborsModal, setShowCardsGroupNeighborsModal] = useState(false);

    const [playersWithCourt, setPlayersWithCourt] = useState([]);
    const [showPlayersWithCourtModal, setShowPlayersWithCourtModal] = useState(false);

    const [cardDescription, setCardDescription] = useState();

    const [cardsWomenNeighbors, setCardsWomenNeighbors] = useState([]);
    const [showCardsWomenNeighborsModal, setShowCardsWomenNeighborsModal] = useState(false);

    const [allPlayedCards, setAllPlayedCards] = useState([]);
    const [showAllPlayedCardsModal, setShowAllPlayedCardsModal] = useState(false);

    const [playersHaveCrusadePoints, setPlayersHaveCrusadePoints] = useState([]);
    const [showPlayersHaveCrusadePointsModal, setShowPlayersHaveCrusadePointsModal] = useState(false);

    const [courtCrusaderNeighbor, setCourtCrusaderNeighbor] = useState([]);
    const [showCourtCrusaderNeighborModal, setShowCourtCrusaderNeighborModal] = useState(false);

    const [modalAlertIsOpen, setmodalAlertIsOpen] = useState(false);

    const [cardsHand, setCardsHand] = useState([]);
    const [showCardsHandModal, setShowCardsHandModal] = useState(false);

    const [cardIdPlay, setCardIdPlay] = useState();









    const [message, setMessage] = useState('');
    const [data, setData] = useState([]);
    const [showModalWithOneChoise, setShowModalWithOneChoise] = useState(false);
    const [handleName, setHandleName] = useState('');

    
    const handleActions = {
        handleCureEpidemic: (id) => {
            handleCureEpidemic(id);
            setShowModalWithOneChoise(false);
        },
        handleStealCrusadePoints: (id) => {
            handleStealCrusadePoints(id);
            setShowModalWithOneChoise(false);
        },
        handleChoiseCourtKill: (id) => {
            handleChoiseCourtKill(id);
            setShowModalWithOneChoise(false);
        },
        handleResetCard: (id) => {
            handleResetCard(id);
            setShowModalWithOneChoise(false);
        },
        handleKillCourt: (id) => {
            handleKillCourt(id);
            setShowModalWithOneChoise(false);
        },
        handleDeleteCardAndGetCrusadePoints: (id) => {
            handleDeleteCardAndGetCrusadePoints(id);
            setShowModalWithOneChoise(false);
        },
    };
















    const fetchPlayers = async() => {
        try {
            const response_players = await axios.get(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/players/`
            );

            setPlayers(response_players.data);
        } catch(error){
            console.error("Ошибка получения пользователей:", error);
        }
    };

    useEffect(() => {
        fetchPlayers();
        setCardDescription(card.card_data.description);
    }, [roomCode, player.id]);

    useEffect(() => {
        if (card.card_group_in_deck === "universal") {
            setIsSpecial(true);
        }
    }, [card]);

    const handleClickItem = async(pl) => {
        if((card.card_data.description === "require_discard_to_settle" && pl.id !== player.id) || (card.card_data.description !== "require_discard_to_settle")){
            try {
                const responce = await axios.post(
                    `http://192.168.1.68:8000/api/rooms/${roomCode}/player/add-played-card/`,{
                        'player_id': pl.id,
                        'id_card_in_deck': card.id,
                    }
                );

                // if(responce.data.epidemics){
                //     setEpidemicOptions(responce.data.epidemics);
                //     setShowEpidemicModal(true);
                //     return;
                // }

                if(responce.data.epidemics){
                    setMessage(responce.data.message);
                    setData(responce.data.epidemics);
                    setHandleName(responce.data.handle);
                    setShowModalWithOneChoise(true);
                    return;
                }

                if(responce.data.court_cards){
                    // setCourtCardsOptions(responce.data.court_cards);
                    // setShowCourtCardsModal(true);
                    setMessage(responce.data.message);
                    setData(responce.data.court_cards);
                    setHandleName(responce.data.handle);
                    setShowModalWithOneChoise(true);
                    setPlayerIdWhereKill(responce.data.player_id_where_kill);
                    return;
                }

                if(responce.data.monks){
                    setMonksOptions(responce.data.monks);
                    setShowMonksModal(true);
                    setPlayerIdToMove(responce.data.player_id_to_move);
                    return;
                }

                if(responce.data.commoner_crusader_cards){
                    setCommonerCrusaderCardsOptions(responce.data.commoner_crusader_cards);
                    setShowCommonerCrusaderCardsModal(true);
                    setPlayerIdToMove(responce.data.player_id_to_move);
                    return;
                }

                if(responce.data.available_groups){
                    setAvailableGroups(responce.data.available_groups);
                    setShowAvailableGroupsModal(true);
                    setNextPlayer(responce.data.neighbors.next_player);
                    setPrevPlayer(responce.data.neighbors.prev_player);
                    return;
                }

                // if(responce.data.players_with_court){
                //     setPlayersWithCourt(responce.data.players_with_court);
                //     setShowPlayersWithCourtModal(true);
                //     setPlayerIdToMove(responce.data.player_id_to_move);
                //     return;
                // }

                if(responce.data.players_with_court){
                    setMessage(responce.data.message);
                    setData(responce.data.players_with_court);
                    setHandleName(responce.data.handle);
                    setShowModalWithOneChoise(true);

                    return;
                }

                if(responce.data.cards_women_neighbors){
                    setCardsWomenNeighbors(responce.data.cards_women_neighbors);
                    setShowCardsWomenNeighborsModal(true);
                    setPlayerIdToMove(responce.data.player_id_to_move);
                    return;
                }

                if(responce.data.all_played_cards){
                    // setAllPlayedCards(responce.data.all_played_cards);
                    // setShowAllPlayedCardsModal(true);
                    setMessage(responce.data.message);
                    setData(responce.data.all_played_cards);
                    setHandleName(responce.data.handle);
                    setShowModalWithOneChoise(true);

                    setPlayerIdWhereKill(responce.data.player_id);
                    return;
                }

                // if(responce.data.players_have_crusade_points){
                //     setPlayersHaveCrusadePoints(responce.data.players_have_crusade_points);
                //     setShowPlayersHaveCrusadePointsModal(true);
                //     setPlayerIdToMove(responce.data.player);
                //     return;
                // }

                if(responce.data.players_have_crusade_points){
                    setMessage(responce.data.message);
                    setData(responce.data.players_have_crusade_points);
                    setHandleName(responce.data.handle);
                    setShowModalWithOneChoise(true);

                    setPlayerIdToMove(responce.data.player);
                    return;
                }

                if(responce.data.court_crusader_neighbor){
                    setCourtCrusaderNeighbor(responce.data.court_crusader_neighbor);
                    setShowCourtCrusaderNeighborModal(true);
                    setPlayerIdToMove(responce.data.player_id);
                    return;
                }
            } catch(error){
                console.error("Ошибка при добавлении карты в город (при розыгрыше): ", error);
            }
            try {
                await axios.delete(
                    `http://192.168.1.68:8000/api/rooms/${roomCode}/player/delete-hand-card/`,{
                        data: {
                            'player_id': player.id,
                            'id_card_in_deck': card.id,
                        }
                        
                    }
                );
            } catch(error){
                console.error("Ошибка при удалении карты с руки: ", error);
            }
            action();
            setSelectedCard(null);
        }
        else if(card.card_data.description === "require_discard_to_settle" && pl.id === player.id){
            // Если рыцарь или конь и игрок хочет поселить в своем городе, то надо сбросить карту с руки
            try{
                const response = await axios.post(
                    `http://192.168.1.68:8000/api/rooms/${roomCode}/require-discard-to-settle/`,
                    {
                        'player_id': player.id,
                        'card_id': card.id
                    }
                );
                if(!response.data.can_play){
                    // сообщение с ошибкой: вы не можете поселить жителя в своем городе, так как нет карт для сброса
                    setmodalAlertIsOpen(true);
                    return;
                }
                else if(response.data.can_play && response.data.cards_hand){
                    // модальное окно с выбором карты, которую надо сбросить
                    // setCardsHand(response.data.cards_hand);
                    // setCardIdPlay(response.data.card_id_play);
                    // setShowCardsHandModal(true);
                    setMessage(response.data.message);
                    setData(response.data.cards_hand);
                    setHandleName(response.data.handle);
                    setShowModalWithOneChoise(true);
                    return;
                }
            } catch(error){
                console.error("Ошибка при розыгрыше карты в своем городе",error);
            }
        }
    };

    const handleCureEpidemic = async (epidemicId) => {
        try {
            await axios.post(`http://192.168.1.68:8000/api/rooms/${roomCode}/cure-epidemic/`, {
                epidemic_id: epidemicId,
            });

            setShowEpidemicModal(false);

            try {
                await axios.delete(
                    `http://192.168.1.68:8000/api/rooms/${roomCode}/player/delete-hand-card/`,{
                        data: {
                            'player_id': player.id,
                            'id_card_in_deck': card.id,
                        }
                        
                    }
                );
            } catch(error){
                console.error("Ошибка при удалении карты с руки: ", error);
            }
            
            action();
            setSelectedCard(null);
        } catch (error) {
            console.error("Ошибка при лечении эпидемии:", error);
        }
    };

    const handleKillCourt = async (courtCardId) => {
        setShowCourtCardsModal(false);

        try{
            const responce = await axios.post(`
                http://192.168.1.68:8000/api/rooms/${roomCode}/kill-court/`, 
                {
                    'player_id_where_kill': playerIdWhereKill,
                    'id_card_in_deck': courtCardId,
                    'card_description': cardDescription,
                }
            );

            if(responce.data.next_player && responce.data.prev_player){
                setPeasantCardsOptions([
                    {
                        player_id: responce.data.next_player.id,
                        city_name: responce.data.next_player.city_name,
                        cards: responce.data.next_player.peasant_cards
                    },
                    {
                        player_id: responce.data.prev_player.id,
                        city_name: responce.data.prev_player.city_name,
                        cards: responce.data.prev_player.peasant_cards
                    }
                ]);
                setPlayerIdToMovePeasantCards(responce.data.player_id_to_move);
                setShowPeasantCardsModal(true);
                return;
            }
            else{
                try {
                    await axios.delete(
                        `http://192.168.1.68:8000/api/rooms/${roomCode}/player/delete-hand-card/`,{
                            data: {
                                'player_id': player.id,
                                'id_card_in_deck': card.id,
                            }
                            
                        }
                    );

                    action();
                    setSelectedCard(null);
                } catch(error){
                    console.error("Ошибка при удалении карты с руки: ", error);
                }
            }
        }catch(error){
            console.error("Ошибка при удалении карты придворного: ", error);
        }
    };

    const handleMovePeasantCard = async (peasantCardId, fromPlayerId) => {
        setShowPeasantCardsModal(false);

        try{
            await axios.post(`http://192.168.1.68:8000/api/rooms/${roomCode}/move-peasant-card/`, {
                'player_id_from_move': fromPlayerId,
                'player_id_to_move': playerIdToMovePeasantCards,
                'id_card_in_deck': peasantCardId,
            });

            try {
                await axios.delete(
                    `http://192.168.1.68:8000/api/rooms/${roomCode}/player/delete-hand-card/`,{
                        data: {
                            'player_id': player.id,
                            'id_card_in_deck': card.id,
                        }
                        
                    }
                );

                action();
                setSelectedCard(null);
            } catch(error){
                console.error("Ошибка при удалении карты с руки: ", error);
            }

        } catch(error){
            console.error("Ошибка при перемещении карты простолюдина: ", error);
        }
    };

    const handleMoveCard = async(player_id_from_move, card_id) => {
        setShowMonksModal(false);
        setShowCommonerCrusaderCardsModal(false);

        try{
            await axios.post(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/move-card/`,
                {
                    'player_id_from_move': player_id_from_move,
                    'player_id_to_move': playerIdToMove,
                    'card_id': card_id
                }
            );

            try {
                await axios.delete(
                    `http://192.168.1.68:8000/api/rooms/${roomCode}/player/delete-hand-card/`,{
                        data: {
                            'player_id': player.id,
                            'id_card_in_deck': card.id,
                        }
                        
                    }
                );

                action();
                setSelectedCard(null);
            } catch(error){
                console.error("Ошибка при удалении карты с руки: ", error);
            }
        }catch(error){
            console.error("Ошибка при перещении карты Монах: ", error);
        }
    };

    const handleChoiseCardToMove = async(group) => {
        setShowAvailableGroupsModal(false);
        
        const response = await axios.post(
            `http://192.168.1.68:8000/api/rooms/${roomCode}/choise-card-to-move/`,
            {
                'next_player_id': nextPlayer.player_id,
                'prev_player_id': prevPlayer.player_id,
                'card_group': group
            }
        );

        if(response.data.cards_group_neighbors){
            setCardsGroupNeighbors(response.data.cards_group_neighbors);
            setShowCardsGroupNeighborsModal(true);
        }
    };

    const handleSwapCards = async(next_player_id, next_player_card_id, prev_player_id, prev_player_card_id) => {
        await axios.post(
            `http://192.168.1.68:8000/api/rooms/${roomCode}/swap-cards/`,
            {
                'next_player_id': next_player_id,
                'next_player_card_id': next_player_card_id,
                'prev_player_id': prev_player_id,
                'prev_player_card_id': prev_player_card_id
            }
        );

        try {
            await axios.delete(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/player/delete-hand-card/`,{
                    data: {
                        'player_id': player.id,
                        'id_card_in_deck': card.id,
                    }
                    
                }
            );

            action();
            setSelectedCard(null);
        } catch(error){
            console.error("Ошибка при удалении карты с руки: ", error);
        }
    };

    const handleChoiseCourtKill = async(player_id) => {
        // setShowPlayersWithCourtModal(false);

        const responce = await axios.post(
            `http://192.168.1.68:8000/api/rooms/${roomCode}/choise-court-kill/`,
            {
                'player_id': player_id
            }
        );

        if(responce.data.court_cards){
            // setCourtCardsOptions(responce.data.court_cards);
            // setShowCourtCardsModal(true);
            // setPlayerIdWhereKill(responce.data.player_id_where_kill);
            setMessage(responce.data.message);
            setData(responce.data.court_cards);
            setHandleName(responce.data.handle);
            setShowModalWithOneChoise(true);
            setPlayerIdWhereKill(responce.data.player_id_where_kill);
            return;
        }
    };

    const handleDeleteCardAndGetCrusadePoints = async (card_id) => {
        setShowAllPlayedCardsModal(false);

        try{
            await axios.post(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/delete-card-and-get-crusade-points/`,
                {
                    'player_id': playerIdWhereKill,
                    'card_id': card_id
                }
            );

            await axios.delete(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/player/delete-hand-card/`,{
                    data: {
                        'player_id': player.id,
                        'id_card_in_deck': card.id,
                    }
                    
                }
            );

            action();
            setSelectedCard(null);
            
        } catch(error){
            console.error("Ошибка при удалении карты и получении ко: ", error);
        }
    };

    const handleStealCrusadePoints = async (pl_id) => {
        // setShowPlayersHaveCrusadePointsModal(false);

        try{
            await axios.post(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/steal-crusade-points/`,
                {
                    'player_id_from': pl_id,
                    'player_id_to': playerIdToMove,
                }
            );

            await axios.delete(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/player/delete-hand-card/`,{
                    data: {
                        'player_id': player.id,
                        'id_card_in_deck': card.id,
                    }
                    
                }
            );

            action();
            setSelectedCard(null);
        } catch(error){
            console.error("Ошибка при воровстве ко: ", error);
        }
    };

    const handleSendCourtCrusaderNeighbor = async (player_id_from, card_id) => {
        setShowCourtCrusaderNeighborModal(false);

        try{
            await axios.post(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/send-court-crusade/`,
                {
                    'player_id_from': player_id_from,
                    'card_id': card_id,
                    'player_id_to': playerIdToMove
                }
            );

            await axios.delete(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/player/delete-hand-card/`,{
                    data: {
                        'player_id': player.id,
                        'id_card_in_deck': card.id,
                    }
                    
                }
            );

            action();
            setSelectedCard(null);
        }catch(error){
            console.error("Ошибка при отправке придворного в крестовый поход: ", error);
        }
    };

    const handleResetCard = async(card_id) => {
        setShowCardsHandModal(false);
        await axios.post(
            `http://192.168.1.68:8000/api/rooms/${roomCode}/reset-card/play-require-discard-to-settle/`,
            {
                'card_id': card_id,
                'player_id': player.id,
                'card_id_play': cardIdPlay
            }
        );
        action();
        setSelectedCard(null);
    };


    if(isSpecial){
        return (
            <PlayingSpecialCards 
                roomCode = {roomCode} 
                card = {card} 
                setIsSpecial = {setIsSpecial}
            />
        )
    }

    return(
        <>
        <Modal
            isOpen={modalAlertIsOpen}
            onRequestClose={() => setmodalAlertIsOpen(false)}
            contentLabel="Уведомление"
            className="modal"
            overlayClassName="modal-overlay"
            >
            <h2>Карту нельзя разыграть в своем городе, так как нет карт для сброса</h2>
        </Modal>

        <Modal 
        isOpen={showModalWithOneChoise} 
        onRequestClose={() => setShowModalWithOneChoise(false)}
        >
            <h2>{message}</h2>
            {data.map(item => (
                <button key={item.id} onClick={() => handleActions[handleName]?.(item.id)}>
                <p>{item.name}</p>
                </button>
            ))}
        </Modal>
        

        <Modal 
        isOpen={showCourtCrusaderNeighborModal} 
        onRequestClose={() => setShowCourtCrusaderNeighborModal(false)}
        >
            <h2>Выберите придворного, которого хотите отправить в крестовый поход:</h2>
            {courtCrusaderNeighbor.map(court_card => (
                <button key={court_card.card_id} onClick={() => handleSendCourtCrusaderNeighbor(court_card.player_id, court_card.card_id)}>
                <p>{court_card.card_name}</p>
                </button>
            ))}
        </Modal>

        <Modal 
            isOpen={showPeasantCardsModal} 
            onRequestClose={() => setShowPeasantCardsModal(false)}
        >
            <h2>Выберите по одному простолюдину из соседних городов:</h2>
            {peasantCardsOptions.map((source) => (
                <div>
                    <h3>Город: {source.city_name}</h3>
                    {source.cards.map((card)=>(
                        <label key={card.id}>
                            <input 
                                type="radio" 
                                name={source.player_id} 
                                value={card.id}
                                onChange={() => {
                                    setSelectedCardsRadio(prev => ({
                                        ...prev,
                                        [source.player_id]: card.id
                                    }));
                                }}
                            />
                            {card.name}
                        </label>
                    ))}
                        
                </div>
            ))}
            <button 
                onClick={() => {
                    Object.entries(selectedCardsRadio).forEach(([player_id, card_id]) => {
                        handleMovePeasantCard(card_id, player_id);
                    });
                    setShowPeasantCardsModal(false);
                }}
            >
                <p>Переместить</p>
            </button>
        </Modal>

        <Modal 
        isOpen={showMonksModal} 
        onRequestClose={() => setShowMonksModal(false)}
        >
            <h2>Выберите город, из которого хотите переманить Монаха:</h2>
            {monksOptions.map(monk => (
                <button key={monk.other_player_id} onClick={() => handleMoveCard(monk.other_player_id, monk.id_card_in_deck)}>
                <p>{monk.other_player_city_name}</p>
                </button>
            ))}
        </Modal>

        <Modal 
        isOpen={showCommonerCrusaderCardsModal} 
        onRequestClose={() => setShowCommonerCrusaderCardsModal(false)}
        >
            <h2>Выберите карту простолюдина крестоносца, которую хотите переместить:</h2>
            {commonerCrusaderCardsOptions.map(card => (
                <button key={card.card_id} onClick={() => handleMoveCard(card.player_id, card.card_id)}>
                <p>{card.card_name}</p>
                </button>
            ))}
        </Modal>

        <Modal 
        isOpen={showAvailableGroupsModal} 
        onRequestClose={() => setShowAvailableGroupsModal(false)}
        >
            <h2>Выбери сословие для обмена жителями между соседними городами {prevPlayer.player_city_name} и {nextPlayer.player_city_name}:</h2>
            {availableGroups.map((group, i) => (
                <button key={i} onClick={() => handleChoiseCardToMove(group.value)}>
                <p>{group.label}</p>
                </button>
            ))}
        </Modal>

        <Modal 
            isOpen={showCardsGroupNeighborsModal} 
            onRequestClose={() => setShowCardsGroupNeighborsModal(false)}
        >
            {cardsGroupNeighbors ? (
                <>
                    <h2>Выберите по одной карте из соседних городов:</h2>
                    <div>
                        <h3>Город: {cardsGroupNeighbors.next_player.player_city_name}</h3>
                        {cardsGroupNeighbors.next_player.cards.map(c => (
                            <label key={c.card_id}>
                                <input
                                type="radio"
                                name={cardsGroupNeighbors.next_player.player_id}
                                value={c.card_id}
                                onChange={() =>
                                    setSelectedCardsRadio(prev => ({
                                    ...prev,
                                    [cardsGroupNeighbors.next_player.player_id]: c.card_id
                                    }))
                                }
                                />
                                {c.card_name}
                            </label>
                        ))}
                    </div>

                    <div>
                        <h3>Город: {cardsGroupNeighbors.prev_player.player_city_name}</h3>
                        {cardsGroupNeighbors.prev_player.cards.map(c => (
                            <label key={c.card_id}>
                                <input
                                type="radio"
                                name={cardsGroupNeighbors.prev_player.player_id}
                                value={c.card_id}
                                onChange={() =>
                                    setSelectedCardsRadio(prev => ({
                                    ...prev,
                                    [cardsGroupNeighbors.prev_player.player_id]: c.card_id
                                    }))
                                }
                                />
                                {c.card_name}
                            </label>
                        ))}
                    </div>
                    <button 
                        onClick={() => {
                            setShowCardsGroupNeighborsModal(false);
                            const next_player_id = cardsGroupNeighbors.next_player.player_id;
                            const prev_player_id = cardsGroupNeighbors.prev_player.player_id;

                            const next_player_card_id = selectedCardsRadio[next_player_id];
                            const prev_player_card_id = selectedCardsRadio[prev_player_id];

                            if (next_player_id && prev_player_id && next_player_card_id && prev_player_card_id) {
                                handleSwapCards(next_player_id, next_player_card_id, prev_player_id, prev_player_card_id);
                            }
                            else{
                                alert(`next_player_id= ${next_player_id}, prev_player_id=${prev_player_id}, next_player_card_id=${next_player_card_id}, prev_player_card_id=${prev_player_card_id}`)
                            }
                        }}
                    >
                        <p>Поменять местами</p>
                    </button>
                </>
            ) : (
                <div>Загрузка данных</div>
            )}
            
        </Modal>


        <Modal 
        isOpen={showCardsWomenNeighborsModal} 
        onRequestClose={() => setShowCardsWomenNeighborsModal(false)}
        >
        {cardsWomenNeighbors &&
        cardsWomenNeighbors.next_player &&
        cardsWomenNeighbors.prev_player ? (
            <>
            <h2>Выберите по одной женщине из соседних городов:</h2>

            <div>
                <h3>Город: {cardsWomenNeighbors.next_player.player_city_name}</h3>
                {cardsWomenNeighbors.next_player.cards.map(c => (
                <label key={c.card_id}>
                    <input
                    type="radio"
                    name={cardsWomenNeighbors.next_player.player_id}
                    value={c.card_id}
                    onChange={() =>
                        setSelectedCardsRadio(prev => ({
                        ...prev,
                        [cardsWomenNeighbors.next_player.player_id]: c.card_id,
                        }))
                    }
                    />
                    {c.card_name}
                </label>
                ))}
            </div>

            <div>
                <h3>Город: {cardsWomenNeighbors.prev_player.player_city_name}</h3>
                {cardsWomenNeighbors.prev_player.cards.map(c => (
                <label key={c.card_id}>
                    <input
                    type="radio"
                    name={cardsWomenNeighbors.prev_player.player_id}
                    value={c.card_id}
                    onChange={() =>
                        setSelectedCardsRadio(prev => ({
                        ...prev,
                        [cardsWomenNeighbors.prev_player.player_id]: c.card_id,
                        }))
                    }
                    />
                    {c.card_name}
                </label>
                ))}
            </div>

            <button
                onClick={() => {
                Object.entries(selectedCardsRadio).forEach(([player_id, card_id]) => {
                    handleMoveCard(player_id, card_id);
                });
                setShowPeasantCardsModal(false);
                }}
            >
                <p>Переместить</p>
            </button>
            </>
        ) : (
            <div>Загрузка данных...</div>
        )}
        </Modal>

          
        <div className='playing-cards'>
            <h2>Выберите город, в котором хотите разыграть <span className='accent'>{card.name}</span>:</h2>
            <div className='list-players'>
                {card.card_data.play_only_in_own_city ? (
                    <div
                    className='item-player'
                    key = {player.id}
                    onClick={() => {handleClickItem(player)}}>
                        {player.city_name}
                    </div>
                ) : (
                    (players.length > 0) ? (
                        players.map((value, i) => (
                            <div 
                            className='item-player' 
                            key={i}
                            onClick={() => {handleClickItem(value)}}>
                                {value.city_name}
                            </div>
                        ))
                    ) : (
                        <div>Загрузка данных...</div>
                    )
                )}
            </div>
        </div>
        </>
    )
}

export default PlayingCards;
