import React, { useEffect, useState }  from 'react';
import '../styles/ConnectionScreen.css';
import Button from './Button.js';
import {QRCodeSVG} from 'qrcode.react';
import axios from 'axios';
import Modal from 'react-modal';

Modal.setAppElement('#root');
 
const ConnectionScreen = ({roomData}) => {
    const roomLink = `http://192.168.1.67:3000/join/${roomData.code}`;

    const [players, setPlayers] = useState([]);

    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [selectedPlayer, setSelectedPlayer] = useState(null);

    useEffect(() => {
        console.log("useEffect");
        const fetchPlayers = async () => {
          try {
            const response = await axios.get(`http://192.168.1.67:8000/api/rooms/${roomData.code}/players/`);
            setPlayers(response.data);
          } catch (error) {
            console.error("Ошибка получения списка игроков", error);
          }
        };
    
        const interval = setInterval(fetchPlayers, 2000);
        fetchPlayers();
    
        return () => clearInterval(interval);
    }, [roomData.code]);

    const handleRemovePlayer = async (playerId) => {
        try {
            await axios.delete(`http://192.168.1.67:8000/api/rooms/${roomData.code}/players/${playerId}/delete/`);
            setPlayers(players.filter(player => player.id !== playerId));
        } catch (error) {
          console.error("Ошибка при удалении игрока", error);
        }
    };

    const handleStartGame = () => {
        if(players.length < 3){
            alert("Игра доступна для 3-4 игроков");
        }
    };
      

    return(
        <>
        <Modal
            isOpen={modalIsOpen}
            onRequestClose={() => setModalIsOpen(false)}
            contentLabel="Подтверждение удаления"
            className="modal"
            overlayClassName="modal-overlay"
            >
            <h2>Удалить игрока "{selectedPlayer?.name}"?</h2>
            <div className="modal-buttons">
                <button onClick={async () => {
                    await handleRemovePlayer(selectedPlayer.id);
                    setModalIsOpen(false);
                }}>Удалить</button>
                <button onClick={() => setModalIsOpen(false)}>Отмена</button>
            </div>
        </Modal>
        <div id="connection-screen">
            <div className='info-block'>
                <div className='connection-method'>
                    <div className='connection-qr'>
                        <QRCodeSVG value={roomLink} size={256} />
                    </div>
                    <div className='connection-href'
                    onClick={() => {
                        navigator.clipboard.writeText(roomLink);
                      }}
                    >
                        <h3>ссылка для подключения:</h3>
                        <div>{roomLink}</div>
                    </div>
                </div>

                <div className='waiting-room'>
                    <div className='players-panel'>
                        <h3>участники:</h3>
                        <div className='connected-players'>
                            {players.map(player => (
                                <div 
                                key={player.id} 
                                className='player-card'
                                onClick={() => {
                                    setSelectedPlayer(player);
                                    setModalIsOpen(true);
                                }}
                                >{player.name}</div>
                            ))}
                        </div>
                    </div>
                    <Button text="Начать" onClick={handleStartGame}/>
                </div>
            </div>
        </div>
        </>
    );
}

export default ConnectionScreen;
