import React, {useState, useEffect} from 'react';
import './styles/Game.css';
import PlayersCity from './components/PlayersCity.js';
import axios from 'axios';

const Game = () => {
    const pathParts = window.location.pathname.split('/');
    const roomCode = pathParts[pathParts.length - 1];

    const [players, setPlayers] = useState(0);

    const [cards, setCards] = useState(0);

    useEffect(() => {
        const fetchPlayers = async () => {
            try {
              const response = await axios.get(`http://192.168.1.67:8000/api/rooms/${roomCode}/players/`);
              setPlayers(response.data);
            } catch (error) {
              console.error("Ошибка получения списка игроков", error);
            }
        };

        fetchPlayers();

    }, [roomCode]);

    useEffect(() => {
        const fetchCards = async () => {
            try {
              const r = await axios.get(`http://192.168.1.67:8000/api/cards/deck/${roomCode}/top3/`);
              setPlayers(r.data);
              console.log(r.data);
            } catch (error) {
              console.error("Ошибка получения первых трех карт", error);
            }
        };

        fetchCards();

    }, [roomCode]);

    const renderPlayerCities = () => {
        const cities = [];

        for(let i = 0; i < players.length; i++){
            cities.push(<PlayersCity player={players[i]}/>);
        }

        console.log(players);

        return cities;
    };

    return(
        <div className='Game'>    
            {renderPlayerCities()}        

            <div className='cross-roat'>
                <div className='deck-cards'>
                    <img src="/images/deck_cards.png"/>
                </div>
                <div className='card'>
                    <img src="/images/lord.png"/>
                </div>
                <div className='card'>
                    <img src="/images/peasant.png"/>
                </div>
                <div className='card'>
                    <img src="/images/inquisitor.png"/>
                </div>
            </div>

        </div>
    )
}

export default Game;
