import React, {useState, useEffect} from 'react';
import './styles/Game.css';
import PlayersCity from './components/PlayersCity.js';
import axios from 'axios';

const Game = () => {
    const pathParts = window.location.pathname.split('/');
    const roomCode = pathParts[pathParts.length - 1];

    const [playersCount, setPlayersCount] = useState(0);

    useEffect(() => {
        const fetchPlayersCount = async () => {
            try {
                const response = await axios.get(`http://192.168.1.67:8000/api/rooms/${roomCode}/players-count/`);
                setPlayersCount(response.data.count); // Предполагаем, что сервер возвращает count с количеством игроков
            } catch (error) {
                console.error("Ошибка при получении количества игроков", error);
            }
        };

        fetchPlayersCount(); // Вызов функции при монтировании компонента

    }, [roomCode]);

    return(
        <div className='Game'>
            <PlayersCity />
            <PlayersCity />
            

            <div className='cross-roat'>
                <div className='deck-cards'>
                    <img src="/images/deck_cards.png"/>
                </div>
                <div className='card'>
                    <img src="/images/lord.png"/>
                </div>
                <div className='card'>
                    <img src="/images/peasant.png"/>
                </div>
                <div className='card'>
                    <img src="/images/priesthood.png"/>
                </div>
            </div>

            <PlayersCity />
        </div>
    )
}

export default Game;
