import { useEffect, useState } from 'react';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';
import ConnectionScreen from './components/ConnectionScreen.js';

function Connection() {
  const [roomData, setRoomData] = useState(null);

  useEffect(() => {
    let hostId = localStorage.getItem('host_id');
    
    // Если нет host_id, создаем новый
    if (!hostId) {
      hostId = uuidv4();
      localStorage.setItem('host_id', hostId);
    }

    // Проверим, есть ли уже комната с таким host_id
    axios.post('http://127.0.0.1:8000/api/rooms/create/', { host: hostId })
      .then(response => {
        console.log("Ответ от бэкенда:", response.data);
        setRoomData(response.data);  // Сохраняем данные комнаты
      })
      .catch(error => {
        console.error("Ошибка создания комнаты", error);
      });
  }, []);  // useEffect с пустым массивом зависимостей, чтобы запускать только при монтировании компонента

  return (
    <div className="Connection">
      {roomData ? (
        <ConnectionScreen roomData={roomData} />
      ) : (
        <p>Создание комнаты...</p>
      )}
    </div>
  );
}

export default Connection;
