import React, { useState } from "react";
import { motion } from "framer-motion";
import "../styles/DiceRoll.css";

const DiceRoll = () => {
    const [isRolling, setIsRolling] = useState(false);

    // Функция для старта подбрасывания
    const rollDice = () => {
        setIsRolling(true);
        // Через 2 секунды остановим анимацию (чтобы кубик "упал")
        setTimeout(() => {
            setIsRolling(false);
        }, 2000);
    };

    return (
        <div className="container-dice" onClick={rollDice}>
            <motion.div
                className="cube"
                animate={{
                    rotateX: isRolling ? [0, 720] : 0, // Поворот по оси X
                    rotateY: isRolling ? [0, 720] : 0, // Поворот по оси Y
                    rotateZ: isRolling ? [0, 360] : 0, // Поворот по оси Z
                }}
                transition={{
                    duration: 2,
                    ease: "easeInOut",
                }}
            >
                <div className="side front one">1</div>
                <div className="side back two">2</div>
                <div className="side right three">3</div>
                <div className="side left four">4</div>
                <div className="side top five">5</div>
                <div className="side bottom six">6</div>
            </motion.div>
        </div>
    );
};

export default DiceRoll;
