import React, {useState, useEffect} from 'react';
import './styles/Game.css';
import PlayersCity from './components/PlayersCity.js';
import axios from 'axios';

const Game = () => {
  const pathParts = window.location.pathname.split('/');
  const roomCode = pathParts[pathParts.length - 1];

  const [players, setPlayers] = useState(0);
  const [cards, setCards] = useState(0);

  const [currentPlayerId, setCurrentPlayerId] = useState(null);

  const socket = new WebSocket(`ws://192.168.1.68:8000/ws/rooms/${roomCode}/`);

  socket.onmessage = (event) => {
    const data = JSON.parse(event.data);
    if (data.action === "player_change") {
      console.log("Текущий пользовать изменился");
      setCurrentPlayerId(data.payload.id);
    }
    else if(data.action === "crossroad_update"){
      
    }
  };


    useEffect(() => {
        const fetchPlayers = async () => {
            try {
              const response = await axios.get(`http://192.168.1.68:8000/api/rooms/${roomCode}/players/`);
              setPlayers(response.data);
            } catch (error) {
              console.error("Ошибка получения списка игроков", error);
            }
        };

        fetchPlayers();

    }, [roomCode]);

    useEffect(() => {
        const fetchCards = async () => {
            try {
              const r = await axios.get(`http://192.168.1.68:8000/api/cards/deck/${roomCode}/top3/`);
              setCards(r.data);
            } catch (error) {
              console.error("Ошибка получения первых трех карт", error);
            }
        };

        fetchCards();

    }, [roomCode]);

    useEffect(() => {
        const fetchCurrentPlayer = async () => {
          try {
            const response_current_player = await axios.get(`http://192.168.1.68:8000/api/rooms/${roomCode}/current-player/`);
            setCurrentPlayerId(response_current_player.data.id);
          } catch (err) {
            console.error("Не удалось получить текущего игрока:", err);
          }
        };
      
        fetchCurrentPlayer();
    }, [roomCode]);

    const renderPlayerCities = () => {
        const cities = [];

        for(let i = 0; i < players.length; i++){
            cities.push(
                <PlayersCity
                  key={players[i].id}
                  player={players[i]}
                  isCurrent={players[i].id === currentPlayerId}
                />
            );
        }

        return cities;
    };

    const renderThreeCards = () => {
        const images_cards = [];

        for(let i = 0; i < cards.length; i++){
            images_cards.push(
                <div className='deck-cards'>
                    <img src={`${cards[i].image}`} alt={cards[i].name} />
                </div>
            );
        }

        return images_cards;
    }

    return(
        <div className='Game'>    
            {renderPlayerCities()}        

            <div className='cross-roat'>
                <div>
                    <img src="/images/deck_cards.png"/>
                </div>
                {renderThreeCards()}
            </div>

        </div>
    )
}

export default Game;
