import React from 'react';
import '../styles/NavMenu.css';

const NavMenu = ({direction = "row"}) =>{
    const NavItem = ({ text }) => <div className="nav-item">{text}</div>;
    
    return (
        <nav className={`nav-menu ${direction}`}>
            <NavItem text="Главная" />
            <NavItem text="Об игре" />
            <NavItem text="Как играть онлайн?" />
            <NavItem text="Правила игры" />
        </nav>
    );
}

export default NavMenu;
