import React, {useState} from 'react';
import "../styles/PlayersCity.css";

const PlayersCity = ({ player, isCurrent }) => {
  const pathParts = window.location.pathname.split('/');
  const roomCode = pathParts[pathParts.length - 1];

  const socket = new WebSocket(`ws://192.168.1.68:8000/ws/rooms/${roomCode}/`);

  const [cards, setCards] = useState([]);

  socket.onmessage = (event) => {
      const data = JSON.parse(event.data);
      if (data.action === "changed_played_cards") {
          console.log("Была разыграна карта");
          if(data.payload.id === player.id){
            setCards(data.payload.played_cards);
            console.log(data.payload.played_cards);
          }
      }
  };

    return (
      <div className={`players-city ${isCurrent ? "current-player" : ""}`}>
        <div className='players-info'>
          <div className='player-name'>{player.name}</div>
          <div className='name-city'>{player.city_name}</div>
        </div>
        <div className='city-info'>
          {(cards.length > 0) ? (
            cards.map((card, i) => {
              return <img src={card.card_data.image}/>
            })
          ): (
            <div></div>
          )}
          <div className='courtiers'></div>
          <div className='priests'></div>
          <div className='commoners'></div>
        </div>
      </div>
    );
};
  

export default PlayersCity;
