import React, {useEffect, useState} from 'react';
import "../styles/PlayerTurn.css";
import axios from 'axios';
import "../styles/SelectFromCrossRoad.css";
import Button from "./Button.js";

const SelectFromCrossRoad = ({roomCode}) => {
    const [cards, setCards] = useState(0);

    useEffect(() => {
        const fetchCards = async () => {
            try {
              const r = await axios.get(`http://192.168.1.68:8000/api/cards/deck/${roomCode}/top3/`);
              setCards(r.data);
            } catch (error) {
              console.error("Ошибка получения первых трех карт", error);
            }
        };

        fetchCards();

    }, [roomCode]);

    const renderThreeCards = () => {
        const images_cards = [];

        for(let i = 0; i < cards.length; i++){
            images_cards.push(
                <div className='card'>
                    <img src={`${cards[i].image}`} alt={cards[i].name} />
                    <Button text="Выбрать"/>
                </div>
            );
        }

        return images_cards;
    }

    return (
        <div className="select-from-crossroad">
            {renderThreeCards()}
        </div>
    );
};
  

export default SelectFromCrossRoad;
