import React from "react";
import { motion } from "framer-motion";
import "../styles/DiceRoll.css";

const DiceRoll = ({ value, isHovered }) => {
    // Определяем угол поворота для каждого числа
    const rotations = {
        1: { x: -180, y: -30 },
        2: { x: -40, y: 150 },
        3: { x: -40, y: -60 },
        4: { x: -40, y: 60 },
        5: { x: -60, y: -30 },
        6: { x: 60, y: -30 },
    };

    return (
        <div className="container-dice">
            <motion.div
                className="cube"
                animate={{
                    rotateX: isHovered ? 720 + rotations[value].x : rotations[value].x,
                    rotateY: isHovered ? 720 + rotations[value].y : rotations[value].y,
                }}
                transition={{
                    duration: 2,
                    ease: "easeInOut",
                }}
            >
                <div className="side front">1</div>
                <div className="side back">2</div>
                <div className="side right">3</div>
                <div className="side left">4</div>
                <div className="side top">5</div>
                <div className="side bottom">6</div>
            </motion.div>
        </div>
    );
};

export default DiceRoll;
