import React, {useEffect, useState} from 'react';
import "../styles/PlayerTurn.css";
import axios from 'axios';
import SelectFromCrossRoad from "./SelectFromCrossRoad.js";
import PlayerCardsDisplay from "./PlayerCardsDisplay.js";

const PlayerTurn = ({ player, roomCode }) => {
    const [handCards, setHandCards] = useState([]);
    const [hasSelected, setHasSelected] = useState(false);
    const [hasFullHand, setHasFullHand] = useState(false);

    const fetchPlayerHand = async () => {
        try {
            const response = await axios.get(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/players/${player.id}/hand/`
            );
            setHandCards(response.data);
            if(setHandCards.length === 2){
                setHasFullHand(true);
            }
        } catch (error) {
            console.error("Ошибка получения карт игрока:", error);
        }
    };

    const fetchAndAddTopCard = async () => {
        try {
            const responseTop = await axios.get(
                `http://192.168.1.68:8000/api/cards/deck/${roomCode}/top/`
            );
            const topCard = responseTop.data;

            await axios.post(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/players/${player.id}/add-hand-card/${topCard.id}/`, {
                    'room_code': roomCode,
                    'player_id': player.id,
                    'card_id': topCard.id
                }
            );
        } catch (error) {
            console.error("Ошибка при получении или добавлении верхней карты:", error);
        }
    };

    useEffect(() => {
        const startTurn = async () => {
            await fetchAndAddTopCard();
            await fetchPlayerHand();
        };

        startTurn();
    }, [roomCode, player.id]);

    const handleCardSelect = async (card) => {
        setHasSelected(true);
        await fetchPlayerHand();
    };

    return (
        <div className="player-turn">
            {!hasFullHand ? (
                <SelectFromCrossRoad
                    roomCode={roomCode}
                    onCardSelect={handleCardSelect}
                    player={player}
                />
            ) : (
                <PlayerCardsDisplay cards={handCards} />
            )}
        </div>
    );
}
  

export default PlayerTurn;
