import { useEffect, useState } from 'react';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';
import ConnectionScreen from './components/ConnectionScreen.js';

function Connection() {
  const [roomData, setRoomData] = useState(null);

  useEffect(() => {
    if(!roomData){
          // Логируем, чтобы увидеть, когда useEffect вызывается
    console.log("useEffect вызывается");

    // Проверяем, есть ли host_id в localStorage
    let hostId = localStorage.getItem('host_id');
    console.log("hostId из localStorage:", hostId);

    // Если нет, генерируем новый ID и сохраняем в localStorage
    if (!hostId) {
      hostId = uuidv4();
      localStorage.setItem('host_id', hostId);
      console.log("Сгенерирован новый hostId:", hostId);
    }

    // Отправляем запрос на создание комнаты
    axios.post('http://127.0.0.1:8000/api/rooms/create/', {
      host: hostId
    })
    .then(response => {
      console.log("Ответ от бэкенда:", response.data);
      setRoomData(response.data);
    })
    .catch(error => {
      console.error("Ошибка создания комнаты", error);
    });
    }

  }, []);

  return (
    <div className="Connection">
      {roomData ? (
        <div>
          <h2>Комната создана!</h2>
          <p>Код комнаты: {roomData.code}</p>
          <p>Ссылка: http://localhost:3000/join/{roomData.code}</p>
          {/* QR-код можно добавить позже */}
        </div>
      ) : (
        <p>Создание комнаты...</p>
      )}
      <ConnectionScreen />
    </div>
  );
}

export default Connection;
