import React, { useState } from "react";
import { motion } from "framer-motion";
import "../styles/DiceRoll.css";

const DiceRoll = () => {
    const [isRolling, setIsRolling] = useState(false);
    const [currentNumber, setCurrentNumber] = useState(1); // Начальное значение - 1

    // Функция для генерации случайного числа от 1 до 6
    const generateRandomNumber = () => Math.floor(Math.random() * 6) + 1;

    // Функция для старта подбрасывания
    const rollDice = () => {
        setIsRolling(true);

        // Генерация случайного числа и установка его
        const randomNumber = generateRandomNumber();
        setCurrentNumber(randomNumber);

        // Через 2 секунды остановим анимацию (чтобы кубик "упал")
        setTimeout(() => {
            setIsRolling(false);
        }, 2000);
    };

    return (
        <div className="container-dice" onClick={rollDice}>
            <motion.div
                className="cube"
                animate={{
                    rotateX: isRolling ? [0, 720] : 0, // Поворот по оси X
                    rotateY: isRolling ? [0, 720] : 0, // Поворот по оси Y
                    rotateZ: isRolling ? [0, 360] : 0, // Поворот по оси Z
                }}
                transition={{
                    duration: 2,
                    ease: "easeInOut",
                }}
            >
                <div className="side front one">{currentNumber === 1 ? 1 : ""}</div>
                <div className="side back two">{currentNumber === 2 ? 2 : ""}</div>
                <div className="side right three">{currentNumber === 3 ? 3 : ""}</div>
                <div className="side left four">{currentNumber === 4 ? 4 : ""}</div>
                <div className="side top five">{currentNumber === 5 ? 5 : ""}</div>
                <div className="side bottom six">{currentNumber === 6 ? 6 : ""}</div>
            </motion.div>
        </div>
    );
};

export default DiceRoll;
