import React, {useState, useEffect} from 'react';
import "../styles/PlayingCards.css";
import axios from 'axios';
import PlayingSpecialCards from "./PlayingSpecialCards.js";
import Modal from 'react-modal';

Modal.setAppElement('#root');

const PlayingCards = ({roomCode, card, action, setSelectedCard}) => {
    

    console.log(card);
    const player = JSON.parse(localStorage.getItem('player'));

    const [players, setPlayers] = useState([]);
    const [isSpecial, setIsSpecial] = useState(false);

    const [epidemicOptions, setEpidemicOptions] = useState([]);
    const [showEpidemicModal, setShowEpidemicModal] = useState(false);

    const [courtCardsOptions, setCourtCardsOptions] = useState([]);
    const [playerIdWhereKill, setPlayerIdWhereKill] = useState();
    const [showCourtCardsModal, setShowCourtCardsModal] = useState(false);

    const [peasantCardsOptions, setPeasantCardsOptions] = useState([]);
    const [playerIdToMovePeasantCards, setPlayerIdToMovePeasantCards] = useState();
    const [showPeasantCardsModal, setShowPeasantCardsModal] = useState(false);
    const [selectedCardsRadio, setSelectedCardsRadio] = useState({});

    const [playerIdToMove, setPlayerIdToMove] = useState();

    const [monksOptions, setMonksOptions] = useState([]);
    const [showMonksModal, setShowMonksModal] = useState(false);

    const [commonerCrusaderCardsOptions, setCommonerCrusaderCardsOptions] = useState([]);
    const [showCommonerCrusaderCardsModal, setShowCommonerCrusaderCardsModal] = useState(false);
    
    const [availableGroups, setAvailableGroups] = useState([]);
    const [showAvailableGroupsModal, setShowAvailableGroupsModal] = useState(false);
    const [nextPlayer, setNextPlayer] = useState([]);
    const [prevPlayer, setPrevPlayer] = useState([]);

    const fetchPlayers = async() => {
        try {
            const response_players = await axios.get(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/players/`
            );

            setPlayers(response_players.data);
        } catch(error){
            console.error("Ошибка получения пользователей:", error);
        }
    };

    useEffect(() => {
        fetchPlayers();
    }, [roomCode, player.id]);

    useEffect(() => {
        if (card.card_group_in_deck === "universal") {
            setIsSpecial(true);
        }
    }, [card]);

    const handleClickItem = async(pl) => {
        try {
            const responce = await axios.post(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/player/add-played-card/`,{
                    'player_id': pl.id,
                    'id_card_in_deck': card.id,
                }
            );

            if(responce.data.epidemics){
                setEpidemicOptions(responce.data.epidemics);
                setShowEpidemicModal(true);
                return;
            }

            if(responce.data.court_cards){
                setCourtCardsOptions(responce.data.court_cards);
                setShowCourtCardsModal(true);
                setPlayerIdWhereKill(responce.data.player_id_where_kill);
                return;
            }

            if(responce.data.monks){
                setMonksOptions(responce.data.monks);
                setShowMonksModal(true);
                setPlayerIdToMove(responce.data.player_id_to_move);
                return;
            }

            if(responce.data.commoner_crusader_cards){
                setCommonerCrusaderCardsOptions(responce.data.commoner_crusader_cards);
                setShowCommonerCrusaderCardsModal(true);
                setPlayerIdToMove(responce.data.player_id_to_move);
                return;
            }

            if(responce.data.available_groups){
                setAvailableGroups(responce.data.available_groups);
                setShowAvailableGroupsModal(true);
                return;
            }
        } catch(error){
            console.error("Ошибка при добавлении карты в город (при розыгрыше): ", error);
        }
        try {
            await axios.delete(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/player/delete-hand-card/`,{
                    data: {
                        'player_id': player.id,
                        'id_card_in_deck': card.id,
                    }
                    
                }
            );
        } catch(error){
            console.error("Ошибка при удалении карты с руки: ", error);
        }
        action();
        setSelectedCard(null);
    };

    const handleCureEpidemic = async (epidemicId) => {
        try {
            await axios.post(`http://192.168.1.68:8000/api/rooms/${roomCode}/cure-epidemic/`, {
                epidemic_id: epidemicId,
            });

            setShowEpidemicModal(false);

            try {
                await axios.delete(
                    `http://192.168.1.68:8000/api/rooms/${roomCode}/player/delete-hand-card/`,{
                        data: {
                            'player_id': player.id,
                            'id_card_in_deck': card.id,
                        }
                        
                    }
                );
            } catch(error){
                console.error("Ошибка при удалении карты с руки: ", error);
            }
            
            action();
            setSelectedCard(null);
        } catch (error) {
            console.error("Ошибка при лечении эпидемии:", error);
        }
    };

    const handleKillCourt = async (courtCardId) => {
        setShowCourtCardsModal(false);

        try{
            const responce = await axios.post(`
                http://192.168.1.68:8000/api/rooms/${roomCode}/kill-court/`, 
                {
                    'player_id_where_kill': playerIdWhereKill,
                    'id_card_in_deck': courtCardId,
                }
            );

            if(responce.data.next_player && responce.data.prev_player){
                setPeasantCardsOptions([
                    {
                        player_id: responce.data.next_player.id,
                        city_name: responce.data.next_player.city_name,
                        cards: responce.data.next_player.peasant_cards
                    },
                    {
                        player_id: responce.data.prev_player.id,
                        city_name: responce.data.prev_player.city_name,
                        cards: responce.data.prev_player.peasant_cards
                    }
                ]);
                setPlayerIdToMovePeasantCards(responce.data.player_id_to_move);
                setShowPeasantCardsModal(true);
                return;
            }
        }catch(error){
            console.error("Ошибка при удалении карты придворного: ", error);
        }
    };

    const handleMovePeasantCard = async (peasantCardId, fromPlayerId) => {
        setShowPeasantCardsModal(false);

        try{
            await axios.post(`http://192.168.1.68:8000/api/rooms/${roomCode}/move-peasant-card/`, {
                'player_id_from_move': fromPlayerId,
                'player_id_to_move': playerIdToMovePeasantCards,
                'id_card_in_deck': peasantCardId,
            });

            try {
                await axios.delete(
                    `http://192.168.1.68:8000/api/rooms/${roomCode}/player/delete-hand-card/`,{
                        data: {
                            'player_id': player.id,
                            'id_card_in_deck': card.id,
                        }
                        
                    }
                );

                action();
                setSelectedCard(null);
            } catch(error){
                console.error("Ошибка при удалении карты с руки: ", error);
            }

        } catch(error){
            console.error("Ошибка при перемещении карты простолюдина: ", error);
        }
    };

    const handleMoveCard = async(player_id_from_move, card_id) => {
        setShowMonksModal(false);
        setShowCommonerCrusaderCardsModal(false);

        try{
            await axios.post(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/move-card/`,
                {
                    'player_id_from_move': player_id_from_move,
                    'player_id_to_move': playerIdToMove,
                    'card_id': card_id
                }
            );

            try {
                await axios.delete(
                    `http://192.168.1.68:8000/api/rooms/${roomCode}/player/delete-hand-card/`,{
                        data: {
                            'player_id': player.id,
                            'id_card_in_deck': card.id,
                        }
                        
                    }
                );

                action();
                setSelectedCard(null);
            } catch(error){
                console.error("Ошибка при удалении карты с руки: ", error);
            }
        }catch(error){
            console.error("Ошибка при перещении карты Монах: ", error);
        }
    };

    const handleChoiseCardToMove = async(group) => {
        showAvailableGroupsModal(false);
        
        const response = await axios.post(
            ``,
            {
                'card_group': group
            }
        );
    };

    if(isSpecial){
        return (
            <PlayingSpecialCards 
                roomCode = {roomCode} 
                card = {card} 
                setIsSpecial = {setIsSpecial}
            />
        )
    }

    return(
        <>
        <Modal 
        isOpen={showEpidemicModal} 
        onRequestClose={() => setShowEpidemicModal(false)}
        >
            <h2>Выберите эпидемию для лечения:</h2>
            {epidemicOptions.map(ep => (
                <button key={ep.id} onClick={() => handleCureEpidemic(ep.id)}>
                <p>{ep.name}</p>
                </button>
            ))}
        </Modal>

        <Modal 
        isOpen={showCourtCardsModal} 
        onRequestClose={() => setShowCourtCardsModal(false)}
        >
            <h2>Выберите придворного, которого хотите убить:</h2>
            {courtCardsOptions.map(court_card => (
                <button key={court_card.id} onClick={() => handleKillCourt(court_card.id)}>
                <p>{court_card.name}</p>
                </button>
            ))}
        </Modal>

        <Modal 
            isOpen={showPeasantCardsModal} 
            onRequestClose={() => setShowPeasantCardsModal(false)}
        >
            <h2>Выберите по одному простолюдину из соседних городов:</h2>
            {peasantCardsOptions.map((source) => (
                <div>
                    <h3>Город: {source.city_name}</h3>
                    {source.cards.map((card)=>(
                        <label key={card.id}>
                            <input 
                                type="radio" 
                                name={source.player_id} 
                                value={card.id}
                                onChange={() => {
                                    setSelectedCardsRadio(prev => ({
                                        ...prev,
                                        [source.player_id]: card.id
                                    }));
                                }}
                            />
                            {card.name}
                        </label>
                    ))}
                        
                </div>
            ))}
            <button 
                onClick={() => {
                    Object.entries(selectedCardsRadio).forEach(([player_id, card_id]) => {
                        handleMovePeasantCard(card_id, player_id);
                    });
                    setShowPeasantCardsModal(false);
                }}
            >
                <p>Переместить</p>
            </button>
        </Modal>

        <Modal 
        isOpen={showMonksModal} 
        onRequestClose={() => setShowMonksModal(false)}
        >
            <h2>Выберите город, из которого хотите переманить Монаха:</h2>
            {monksOptions.map(monk => (
                <button key={monk.other_player_id} onClick={() => handleMoveCard(monk.other_player_id, monk.id_card_in_deck)}>
                <p>{monk.other_player_city_name}</p>
                </button>
            ))}
        </Modal>

        <Modal 
        isOpen={showCommonerCrusaderCardsModal} 
        onRequestClose={() => setShowCommonerCrusaderCardsModal(false)}
        >
            <h2>Выберите карту простолюдина крестоносца, которую хотите переместить:</h2>
            {commonerCrusaderCardsOptions.map(card => (
                <button key={card.card_id} onClick={() => handleMoveCard(card.player_id, card.card_id)}>
                <p>{card.card_name}</p>
                </button>
            ))}
        </Modal>

        <Modal 
        isOpen={showAvailableGroupsModal} 
        onRequestClose={() => setShowAvailableGroupsModal(false)}
        >
            <h2>Выбери сословие для обмена жителями между соседними городами:</h2>
            {availableGroups.map((group, i) => (
                <button key={i} onClick={() => handleChoiseCardToMove(group.label)}>
                <p>{group.value}</p>
                </button>
            ))}
        </Modal>
          
        <div className='playing-cards'>
            <h2>Выберите город, в котором хотите разыграть <span className='accent'>{card.name}</span>:</h2>
            <div className='list-players'>
                {(players.length > 0) ? (
                    players.map((value, i) => (
                        <div 
                            className='item-player' 
                            key={i}
                            onClick={() => {handleClickItem(value)}}
                        >
                            {value.city_name}
                        </div>
                    ))
                ) : (
                    <div>Загрузка данных...</div>
                )}
            </div>
            
        </div>
        </>
    )
}

export default PlayingCards;
