import React, { useState } from "react";
import { motion } from "framer-motion";
import "./DiceRoll.css";

const DiceRoll = () => {
    const [rolling, setRolling] = useState(false);

    const rollDice = () => {
        setRolling(true);
        setTimeout(() => setRolling(false), 1000);
    };

    return (
        <div className="dice-container">
            <motion.div
                className="dice"
                animate={{
                    rotateX: rolling ? [0, 720] : 0,
                    rotateY: rolling ? [0, 720] : 0,
                    rotateZ: rolling ? [0, 720] : 0,
                    y: rolling ? [-50, 0] : 0
                }}
                transition={{ duration: 1, ease: "easeOut" }}
            >
                üé≤
            </motion.div>
            <button onClick={rollDice} className="roll-button">–ë—Ä–æ—Å–∏—Ç—å</button>
        </div>
    );
};

export default DiceRoll;
