import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import "../styles/DiceRoll.css";

const DiceRoll = ({ value}) => {
    const [rotation, setRotation] = useState(getRotationForNumber(value)); // Начальное положение
    const [isRolling, setIsRolling] = useState(false);

    // Функция для получения правильного угла поворота для определённого числа
    function getRotationForNumber(number) {
        switch (number) {
            case 1: return { x: 0, y: 0, z: 0 };  // Грань "1" спереди
            case 2: return { x: 0, y: 180, z: 0 }; // Грань "2" спереди
            case 3: return { x: 0, y: -90, z: 0 }; // Грань "3" спереди
            case 4: return { x: 0, y: 90, z: 0 };  // Грань "4" спереди
            case 5: return { x: 90, y: 0, z: 0 };  // Грань "5" спереди
            case 6: return { x: -90, y: 0, z: 0 }; // Грань "6" спереди
            default: return { x: 0, y: 0, z: 0 };
        }
    }

    // Обновляем начальное положение кубика при изменении `value`
    useEffect(() => {
        setRotation(getRotationForNumber(value));
    }, [value]);

    // Функция для подбрасывания
    const rollDice = () => {
        setIsRolling(true);

        // Через 2 секунды останавливаем анимацию на `value`
        setTimeout(() => {
            setRotation(getRotationForNumber(value));
            setIsRolling(false);
        }, 2000);
    };

    return (
        <div className="container-dice" onClick={rollDice()}>
            <motion.div
                className="cube"
                animate={{
                    rotateX: isRolling ? [0, 720] : rotation.x,
                    rotateY: isRolling ? [0, 720] : rotation.y,
                    rotateZ: isRolling ? [0, 360] : rotation.z,
                }}
                transition={{
                    duration: 2,
                    ease: "easeInOut",
                }}
            >
                <div className="side front one">1</div>
                <div className="side back two">2</div>
                <div className="side right three">3</div>
                <div className="side left four">4</div>
                <div className="side top five">5</div>
                <div className="side bottom six">6</div>
            </motion.div>
        </div>
    );
};

export default DiceRoll;
