import React from 'react';
import '../styles/ConnectionScreen.css';
import Button from './Button.js';

const ConnectionScreen = ({ roomData }) => {
  return(
    <div id="connection-screen">
      <div className='info-block'>
        <div className='connectiom-method'>
          <p>Код комнаты: {roomData.code}</p>
          <p>Ссылка: <a href={`http://localhost:3000/join/${roomData.code}`} target="_blank" rel="noopener noreferrer">http://localhost:3000/join/{roomData.code}</a></p>
          {/* Здесь можно добавить QR-код */}
        </div>

        <div className='waiting-room'>
          <div className='players-panel'>
            {/* Здесь можно отобразить игроков, если нужно */}
          </div>
          <Button text="Начать"/>
        </div>
      </div>
    </div>
  );
}

export default ConnectionScreen;
