import React, { useState } from "react";
import "../styles/GameGuide.css";
import DiceRoll from "./DiceRoll";

const GameGuide = () => {
    const [rollingIndex, setRollingIndex] = useState(null);

    return (
        <div id="game-guide">
            <div className="wrapper">
                <h2>как играть онлайн?</h2>
                <div className="game-guide-description">
                    Смартфон - ваш личный «средневековый арсенал», где вы видите свои карты, ресурсы и выбираете действия 
                    (хоть рыцарский удар, хоть чумной саботаж!). А чтобы следить за общим хаосом — дополнительное устройство с 
                    большим экраном (ноутбук, планшет или умный холодильник).
                </div>
                <div className="steps-game">
                    {[1, 2, 3].map((value, index) => (
                        <div
                            key={value}
                            className="step-game"
                            onMouseEnter={() => setRollingIndex(index)}
                            onMouseLeave={() => setRollingIndex(null)}
                        >
                            <DiceRoll value={value} isRolling={rollingIndex === index} />
                            <h4>{value === 1 ? "Создайте комнату" : value === 2 ? "Соберите друзей" : "Начните игру"}</h4>
                            <div>
                                {value === 1
                                    ? "Создайте комнату c помощью дополнительного устройства"
                                    : value === 2
                                    ? "Отсканируйте с мобильных устройств QR-код"
                                    : "Нажмите кнопку 'играть', когда все будут в сборе"}
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default GameGuide;
