import React, {useState, useEffect} from 'react';
import '../styles/Header.css';

/**
 * Компонент Header отвечает за отображение шапки сайта.
 * Включает в себя адаптивное меню для мобильных устройств.
 */
const Header = () => {
  // Состояние для отслеживания открытия/закрытия меню
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Состояние для определения, находится ли пользователь на мобильном устройстве
  const [isMobile, setIsMobile] = useState(false);

  // Эффект для обработки изменения размера окна и определения типа устройства
  useEffect(()=>{
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 650);
    };

    // Вызов функции при монтировании компонента
    handleResize();

    // Добавление слушателя события resize
    window.addEventListener('resize', handleResize);

    // Очистка слушателя при размонтировании компонента
    return () => {
      window.removeEventListener('resize', handleResize);
    }
  },[]);

  /**
   * Функция для переключения состояния меню (открыто/закрыто).
   */
  const toggleMenu = ()=>{
    setIsMenuOpen(!isMenuOpen);
  }
  
  return (
    <header id="header">
      {isMobile ? (
        <>
        {/* Кнопка для открытия/закрытия меню на мобильных устройствах */}
        <button className="hamburger" onClick={toggleMenu}>
          <div className='hamburger-lines'>
            <span className={`hamburger-line ${isMenuOpen ? 'open' : ''}`}></span>
            <span className={`hamburger-line ${isMenuOpen ? 'open' : ''}`}></span>
            <span className={`hamburger-line ${isMenuOpen ? 'open' : ''}`}></span>
          </div>
        </button>
        
        {/* Мобильное меню, отображаемое при открытом состоянии */}
        {isMenuOpen && (
          <div className="mobile-menu">
            <button className='close-mobile-menu' onClick={toggleMenu}>
              <div className='close-mobile-menu-cross'>
                <span className='cross-line'></span>
                <span className='cross-line'></span>
              </div>
            </button>
            <div>Главная</div>
            <div>Об игре</div>
            <div>Как играть онлайн?</div>
            <div>Правила игры</div>
          </div>
        )}
        </>
      ) : (
        /* Навигационное меню для десктопных устройств */
        <div className='nav-menu'>
          <div>Главная</div>
          <div>Об игре</div>
          <div>Как играть онлайн?</div>
          <div>Правила игры</div>
        </div>
      )}
      
    </header>
    );
  };
  
  export default Header;
