import React, {useEffect, useState} from 'react';
import "../styles/PlayerTurn.css";
import axios from 'axios';
import SelectFromCrossRoad from "./SelectFromCrossRoad.js";

const PlayerTurn = ({ player, roomCode }) => {
    const [selectedCard, setSelectedCard] = useState(null);


    useEffect(() => {
        const fetchCard = async () => {
            try {
              const response_top_card = await axios.get(`http://192.168.1.68:8000/api/cards/deck/${roomCode}/top/`);
              console.log(response_top_card);
            } catch (error) {
              console.error("Ошибка получения верхней карты из колоды", error);
            }
        };

        fetchCard();

    }, [roomCode]);

    const handleCardSelect = (card) => {
        setSelectedCard(card);
    };

    return (
        <div className="player-turn">
            {!selectedCard ? (
                <SelectFromCrossRoad 
                    roomCode = {roomCode}
                    onCardSelect={handleCardSelect}
                />
            ) : (
                <div>Выбор какую карту сыграть</div>
            )}
            
        </div>
    );
  };
  

export default PlayerTurn;
