import React from "react";
import { motion } from "framer-motion";
import "../styles/DiceRoll.css";

const DiceRoll = ({ value, isRolling }) => {
    return (
        <div className="container-dice">
            <motion.div
                className="cube"
                animate={{
                    rotateX: isRolling ? [0, 720] : 0,
                    rotateY: isRolling ? [0, 720] : 0,
                    rotateZ: isRolling ? [0, 360] : 0,
                }}
                transition={{
                    duration: 2,
                    ease: "easeInOut",
                }}
            >
                <div className="side front">{value === 1 ? "1" : ""}</div>
                <div className="side back">{value === 2 ? "2" : ""}</div>
                <div className="side right">{value === 3 ? "3" : ""}</div>
                <div className="side left">{value === 4 ? "4" : ""}</div>
                <div className="side top">{value === 5 ? "5" : ""}</div>
                <div className="side bottom">{value === 6 ? "6" : ""}</div>
            </motion.div>
        </div>
    );
};

export default DiceRoll;
