import React, { useState } from "react";
import { motion } from "framer-motion";
import "../styles/DiceRoll.css";

const DiceRoll = () => {
    const [rotate, setRotate] = useState(0);

    const handleRoll = () => {
        setRotate(prevRotate => prevRotate + 90); // Увеличиваем угол вращения на 90 градусов
    };

    return (
        <div className="container-dice">
            <motion.div
                className="cube"
                animate={{ rotateY: rotate }} // Вращаем кубик по оси Y
                transition={{ duration: 1 }} // Время анимации
                onClick={handleRoll} // При клике будет вращение
            >
                <div className="side front one">1</div>
                <div className="side back two">2</div>
                <div className="side right three">3</div>
                <div className="side left four">4</div>
                <div className="side top five">5</div>
                <div className="side bottom six">6</div>
            </motion.div>
        </div>
    );
};

export default DiceRoll;
