import React from 'react';
import '../styles/RulesGame.css';
import CourseGame from './CourseGame.js';

const RulesGame = () =>{

    const showRuleGame = (id) => {
        const element = document.getElementById(id);

        element.innerHTML(`<${id} />`);
    }
    const NavRuleGameItem = (text, ruleGameId) => {
        return(
            <div className="nav-rule-game" onClick={() => showRuleGame(ruleGameId)}>
                {text}
            </div>
        );
        
    }
    return (
        <div className='wrapper'>
            <div className='nav-rules-game'>
                    <NavRuleGameItem text="Ход игры" ruleGameId = "CourseGame" />
                    <div className='nav-rule-game course-game'>Ход игры</div>
                    <div className='nav-rule-game description-maps'>Описание карт</div>
                    <div className='nav-rule-game epidemic'>Эпидемия</div>
                    <div className='nav-rule-game crusade'>Крестовый поход</div>
                    <div className='nav-rule-game end-game'>Конец игры</div>
                </div>
            <div id="rules-game">
            </div>
        </div>
        
    )
}

export default RulesGame;
