import React, { useState } from "react";
import { motion } from "framer-motion";
import "../styles/DiceRoll.css";

const DiceRoll = () => {
    const [rolling, setRolling] = useState(false);
    const [rotation, setRotation] = useState({ x: 0, y: 0 });

    const rollDice = () => {
        if (rolling) return; // Чтобы не кликать повторно

        setRolling(true);

        // Генерируем случайные вращения для имитации броска
        const newRotation = {
            x: 720 + Math.random() * 360, // Два полных оборота + случайный угол
            y: 720 + Math.random() * 360
        };

        setRotation(newRotation);

        setTimeout(() => setRolling(false), 1200); // Остановить анимацию через 1.2 сек
    };

    return (
        <div className="dice-container">
            <motion.div
                className="dice"
                animate={{
                    rotateX: rolling ? rotation.x : 0, 
                    rotateY: rolling ? rotation.y : 0,
                    y: rolling ? [-100, 0] : 0 // Подбрасывание вверх и падение
                }}
                transition={{ duration: 1.2, ease: "easeOut" }}
            >
                <div className="cube">
                    <div className="face front">1</div>
                    <div className="face back">6</div>
                    <div className="face right">3</div>
                    <div className="face left">4</div>
                    <div className="face top">5</div>
                    <div className="face bottom">2</div>
                </div>
            </motion.div>
            <button onClick={rollDice} className="roll-button">Бросить</button>
        </div>
    );
};

export default DiceRoll;
