import React, {useState, useEffect} from 'react';
import "../styles/PlayingCards.css";
import axios from 'axios';

const PlayingCards = ({roomCode, card}) => {
    const player = JSON.parse(localStorage.getItem('player'));

    const [players, setPlayers] = useState([]);

    const fetchPlayers = async() => {
        try {
            const response_players = await axios.get(
                `http://192.168.1.68:8000/api/rooms/${roomCode}/players/`
            );

            setPlayers(response_players.data);
        } catch(error){
            console.error("Ошибка получения пользователей:", error);
        }
    };

    useEffect(() => {
        fetchPlayers();
    }, [roomCode, player.id]);

    const handleClickItem = (pl) => {
        // пост запрос на бэк: найти игрока pl в комнате с roomCode в карту города добавить карту card
        // удалить карту с руки
        // вернуться на предыдщий экран
    };

    return(
        <div className='playing-cards'>
            <h2>Выберите город, в котором хотите разыграть <span className='accent'>{card.name}</span>:</h2>
            <div className='list-players'>
                {(players.length > 0) ? (
                    players.map((value, i) => (
                        <div 
                            className='item-player' 
                            key={i}
                            onClick={() => {handleClickItem(value)}}
                        >
                            {value.city_name}
                        </div>
                    ))
                ) : (
                    <div>Загрузка данных...</div>
                )}
            </div>
            
        </div>
    )
}

export default PlayingCards;
