import React, { useState } from "react";
import { motion } from "framer-motion";
import "../styles/DiceRoll.css";

const DiceRoll = () => {
    const [rolling, setRolling] = useState(false);

    const rollDice = () => {
        setRolling(true);
        setTimeout(() => setRolling(false), 1200); // Завершаем анимацию через 1.2 сек
    };

    return (
        <div className="dice-container">
            <motion.div
                className="dice"
                animate={{
                    rotateX: rolling ? [0, 1080] : 0, // 3 оборота по X
                    rotateY: rolling ? [0, 1080] : 0, // 3 оборота по Y
                    rotateZ: rolling ? [0, 360] : 0, // Дополнительный поворот
                    y: rolling ? [-80, 0] : 0 // Подпрыгивание
                }}
                transition={{ duration: 1.2, ease: "easeOut" }}
            >
                <div className="cube">
                    <div className="face front">1</div>
                    <div className="face back">6</div>
                    <div className="face right">3</div>
                    <div className="face left">4</div>
                    <div className="face top">5</div>
                    <div className="face bottom">2</div>
                </div>
            </motion.div>
            <button onClick={rollDice} className="roll-button">Бросить</button>
        </div>
    );
};

export default DiceRoll;
